import React from "react";
import DatePicker from "react-datepicker";
import TimePicker from 'react-time-picker';
import { useState } from "react";

import classes from './CreateInterview.module.css';
import 'react-datepicker/dist/react-datepicker.css';
import 'react-time-picker/dist/TimePicker.css';


const CreateInterview = () => {
    const [date, setDate] = useState(new Date());
    const [time, setTime] = useState('10:00');

    const dateHandler = date => setDate(date);
    const timeHandler = time => setTime(time);

    const onFormSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.startDate)
    }

    return (
        <div className={classes.container}>
            <div className={classes.heading}>
                <h2>Schedule an Interview</h2>
            </div>
            <form onSubmit={ onFormSubmit }>
                <div className={classes.date}>
                    <h3>Enter Date</h3>
                    <DatePicker selected={date} onChange={dateHandler} />
                </div>
                <div className={classes.date}>
                    <h3>Enter Start Time</h3>
                    <TimePicker selected={time} onChange={timeHandler} />
                </div>  
                <div className={classes.date}>
                    <h3>Enter End Time</h3>
                    <TimePicker selected={time} onChange={timeHandler} />
                </div>             
            </form>
        </div>
    );
};

export default CreateInterview;
