import React from "react";
import DesktopDatePicker from '@mui/lab/DesktopDatePicker';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import TimePicker from '@mui/lab/TimePicker';

import Multiselect from 'multiselect-react-dropdown';
import { useState, useEffect } from "react";

import classes from './CreateInterview.module.css';
import 'react-datepicker/dist/react-datepicker.css';


const CreateInterview = () => {
    const [date, setDate] = useState(new Date('2022-03-14T21:00:00'));
    const [startTime, setStartTime] = useState(new Date('2022-03-14T21:00:00'));
    const [endTime, setEndTime] = useState(new Date('2022-03-14T22:00:00'));

    const [interviewerList, setInterviewerList] = useState([])
    const [participantList, setParticpantList] = useState([])
    const [loading, setLoading] = useState(true)

    let formData = {
        "date": "",
        "startTime": "",
        "endTime": "",
        "interviewers": [],
        "participants": []
    }

    useEffect(() => {
        fetch("http://localhost:5000/api/v1/interviewers")
        .then(response => response.json())
        .then(data => {
            setInterviewerList(data.interviewer)
            setLoading(false)
        })
    }, [])

    useEffect(() => {
        fetch("http://localhost:5000/api/v1/participants")
        .then(response => response.json())
        .then(data => {
            setParticpantList(data.participant)
            setLoading(false)
        })
    }, [])

    let selectedValues = [];

    const handleDateChange = (newValue) => {
        setDate(newValue);
    };

    const handleStartTimeChange = (newValue) => {
        setStartTime(newValue);
    };

    const handleEndTimeChange = (newValue) => {
        setEndTime(newValue);
    };

    const onFormSubmit = (e) => {
        e.preventDefault();
        console.log(date);
        console.log(startTime);
        console.log(endTime);
    };

    return (
        <div className={classes.container}>
            <div className={classes.heading}>
                <h2>Schedule an Interview</h2>
            </div>
            {!loading && <form onSubmit={ onFormSubmit }>
                <LocalizationProvider dateAdapter={AdapterDateFns}>
                    <Stack spacing={3}>
                        <DesktopDatePicker
                            label="Select Date"
                            inputFormat="MM/dd/yyyy"
                            value={date}
                            onChange={handleDateChange}
                            renderInput={(params) => <TextField {...params} />}
                        />
                        <TimePicker
                            label="Start Time"
                            value={startTime}
                            onChange={handleStartTimeChange}
                            renderInput={(params) => <TextField {...params} />}
                        />
                        <TimePicker
                            label="End Time"
                            value={endTime}
                            onChange={handleEndTimeChange}
                            renderInput={(params) => <TextField {...params} />}
                        />
                    </Stack>
                </LocalizationProvider>
                <div className={classes.dropdown}>
                    <h4>Choose Interviewers</h4>
                    <Multiselect
                        options={interviewerList}
                        selectedValues={selectedValues} // Preselected value to persist in dropdown
                        displayValue="name"
                    />
                    <br />
                    <h4>Choose Participants</h4>
                    <Multiselect
                        options={participantList}
                        selectedValues={selectedValues} // Preselected value to persist in dropdown
                        displayValue="name"
                    />
                </div>
                <button className={classes.button} type="submit">Create Interview</button>
            </form>}
        </div>
    );
};

export default CreateInterview;
