import React from "react";
import DesktopDatePicker from '@mui/lab/DesktopDatePicker';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import TimePicker from '@mui/lab/TimePicker';

import Multiselect from 'multiselect-react-dropdown';
import { useState, useEffect } from "react";

import classes from './CreateInterview.module.css';
import 'react-datepicker/dist/react-datepicker.css';


const CreateInterview = () => {
    const [value, setValue] = useState(new Date('2014-08-18T21:11:54'));

    const [interviewerList, setInterviewerList] = useState([])
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        fetch("http://localhost:5000/api/v1/interviewers")
        .then(response => response.json())
        .then(data => {
            setInterviewerList(data.interview)
            setLoading(false)
        })
    }, [])

    let selectedValues = [];

    const handleChange = (newValue) => {
        setValue(newValue);
    };

    const onSelect = (SelectedList, SelectedItem) => {

    };

    const onRemove = (SelectedList, SelectedItem) => {

    };

    const onFormSubmit = (e) => {
        e.preventDefault();
        console.log(value);
    };

    return (
        <div className={classes.container}>
            <div className={classes.heading}>
                <h2>Schedule an Interview</h2>
            </div>
            {!loading && <form onSubmit={ onFormSubmit }>
                <LocalizationProvider dateAdapter={AdapterDateFns}>
                    <Stack spacing={3}>
                        <DesktopDatePicker
                        label="Select Date"
                        inputFormat="MM/dd/yyyy"
                        value={value}
                        onChange={handleChange}
                        renderInput={(params) => <TextField {...params} />}
                        />
                        <TimePicker
                        label="Start Time"
                        value={value}
                        onChange={handleChange}
                        renderInput={(params) => <TextField {...params} />}
                        />
                        <TimePicker
                        label="End Time"
                        value={value}
                        onChange={handleChange}
                        renderInput={(params) => <TextField {...params} />}
                        />
                    </Stack>
                </LocalizationProvider>
                <div className={classes.dropdown}>
                    <h4>Choose Interviewers</h4>
                    <Multiselect
                        options={interviewerList}
                        selectedValues={selectedValues} // Preselected value to persist in dropdown
                        onSelect={onSelect}
                        onRemove={onRemove}
                        displayValue="name"
                    />
                    <br />
                    <h4>Choose Participants</h4>
                    <Multiselect
                        options={interviewerList}
                        selectedValues={selectedValues} // Preselected value to persist in dropdown
                        onSelect={onSelect}
                        onRemove={onRemove}
                        displayValue="name"
                    />
                </div>
            </form>}
        </div>
    );
};

export default CreateInterview;
